import React from 'react';
import Modal from 'react-modal';

// Set the app element
Modal.setAppElement('#root');

const AddClientModal = ({ isOpen, onRequestClose, onSubmit, client, setClient, isEditing }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setClient({ ...client, [name]: value });
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel={isEditing ? "Edit Client" : "Add New Client"}
      className="Modal"
      overlayClassName="Overlay"
    >
      <h2>{isEditing ? "Edit Client" : "Add New Client"}</h2>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="clientName">Name</label>
          <input
            type="text"
            id="clientName"
            name="name"
            className="form-control"
            placeholder="Enter client name"
            value={client.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="clientEmail">Email</label>
          <input
            type="email"
            id="clientEmail"
            name="email"
            className="form-control"
            placeholder="Enter client email"
            value={client.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="clientPhone">Phone</label>
          <input
            type="text"
            id="clientPhone"
            name="phone"
            className="form-control"
            placeholder="Enter client phone"
            value={client.phone}
            onChange={handleChange}
          />
        </div>
        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" onClick={onRequestClose}>Close</button>
          <button type="submit" className="btn btn-primary">{isEditing ? "Save Changes" : "Add Client"}</button>
        </div>
      </form>
    </Modal>
  );
};

export default AddClientModal;
