import React, { useState, useEffect } from 'react';
import { fetchProjects, createProject, updateProject } from '../services/api';
import AddProjectModal from './AddProjectModal';
import './Modal.css';
import './ProjectList.css';  // Import the new CSS file

const ProjectList = () => {
  const [projects, setProjects] = useState([]);
  const [currentProject, setCurrentProject] = useState({ name: '', client: '', startDate: '', endDate: '' });
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const getProjects = async () => {
      try {
        const data = await fetchProjects();
        setProjects(data);
      } catch (error) {
        console.error(error);
      }
    };
    getProjects();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!currentProject.name || !currentProject.client) {
      alert('Project name and client name are required.');
      return;
    }

    try {
      if (isEditing) {
        await updateProject(currentProject);
        setProjects(projects.map(p => (p.id === currentProject.id ? currentProject : p)));
      } else {
        const createdProject = await createProject(currentProject);
        setProjects([...projects, createdProject]);
      }
      setCurrentProject({ name: '', client: '', startDate: '', endDate: '' });
      setIsModalOpen(false);
    } catch (error) {
      console.error(error);
    }
  };

  const openModalForEdit = (project) => {
    setCurrentProject(project);
    setIsEditing(true);
    setIsModalOpen(true);
  };

  const openModalForAdd = () => {
    setCurrentProject({ name: '', client: '', startDate: '', endDate: '' });
    setIsEditing(false);
    setIsModalOpen(true);
  };

  return (
    <div className="project-list-container">
      <div className="project-table-wrapper">
        <h1>Project List</h1>
        <table className="project-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Client</th>
              <th>Start Date</th>
              <th>End Date</th>
            </tr>
          </thead>
          <tbody>
            {projects.map((project) => (
              <tr key={project.id} onClick={() => openModalForEdit(project)}>
                <td>{project.name}</td>
                <td>{project.client}</td>
                <td>{project.startDate}</td>
                <td>{project.endDate}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="button-container">
          <button onClick={openModalForAdd}>Add New Project</button>
        </div>
      </div>
      <AddProjectModal
        isOpen={isModalOpen}
        onRequestClose={() => setIsModalOpen(false)}
        onSubmit={handleSubmit}
        project={currentProject}
        setProject={setCurrentProject}
        isEditing={isEditing}
      />
    </div>
  );
};

export default ProjectList;
