import React, { useEffect, useState } from 'react';
import Modal from 'react-modal';
import ProjectDetailModal from './ProjectDetailModal';
import { fetchClientProjects } from '../services/api';
import './ClientProfileModal.css';

// Set the app element
Modal.setAppElement('#root');

const ClientProfileModal = ({ isOpen, onRequestClose, client }) => {
  const [projects, setProjects] = useState([]);
  const [isProjectModalOpen, setIsProjectModalOpen] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);

  useEffect(() => {
    if (client && client.id) {
      const getProjects = async () => {
        try {
          const data = await fetchClientProjects(client.id);
          setProjects(data);
        } catch (error) {
          console.error(error);
        }
      };
      getProjects();
    }
  }, [client]);

  const openProjectModal = (project) => {
    setSelectedProject(project);
    setIsProjectModalOpen(true);
    onRequestClose();  // Close the client profile modal
  };

  return (
    <div>
      <Modal
        isOpen={isOpen}
        onRequestClose={onRequestClose}
        contentLabel="Client Profile"
        className="Modal"
        overlayClassName="Overlay"
      >
        <div className="client-list-container">
          <h2>Client Profile</h2>
          {client && (
            <div>
              <p><strong>Name:</strong> {client.name}</p>
              <p><strong>Email:</strong> {client.email}</p>
              <p><strong>Phone:</strong> {client.phone}</p>

              <h3>Projects</h3>
              {projects.length > 0 ? (
                <table className="project-table table table-striped">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Start Date</th>
                      <th>End Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {projects.map(project => (
                      <tr key={project.id}>
                        <td>
                          <button
                            className="link-button"
                            onClick={() => openProjectModal(project)}
                          >
                            {project.name}
                          </button>
                        </td>
                        <td>{project.startDate}</td>
                        <td>{project.endDate}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <p>No projects found for this client.</p>
              )}

              <h3>Invoices</h3>
              {/* Add invoice information here */}

              <h3>Notes</h3>
              {/* Add notes information here */}
            </div>
          )}
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onRequestClose}>Close</button>
          </div>
        </div>
      </Modal>
      <ProjectDetailModal
        isOpen={isProjectModalOpen}
        onRequestClose={() => setIsProjectModalOpen(false)}
        project={selectedProject}
        client={client}
      />
    </div>
  );
};

export default ClientProfileModal;
