import React, { useState, useEffect } from 'react';
import { fetchProjects, createProject, updateProject, deleteProject } from '../services/api';
import AddProjectModal from './AddProjectModal';
import ConfirmDeleteModal from './ConfirmDeleteModal';
import Layout from './Layout';
import './Modal.css';
import './ProjectList.css';  // Import the new CSS file

const ProjectList = () => {
  const [projects, setProjects] = useState([]);
  const [currentProject, setCurrentProject] = useState({ name: '', client: '', startDate: '', endDate: '' });
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [projectToDelete, setProjectToDelete] = useState(null);

  useEffect(() => {
    const getProjects = async () => {
      try {
        const data = await fetchProjects();
        setProjects(data);
      } catch (error) {
        console.error(error);
      }
    };
    getProjects();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!currentProject.name || !currentProject.client) {
      alert('Project name and client name are required.');
      return;
    }

    try {
      if (isEditing) {
        await updateProject(currentProject);
        setProjects(projects.map(p => (p.id === currentProject.id ? currentProject : p)));
      } else {
        const createdProject = await createProject(currentProject);
        setProjects([...projects, createdProject]);
      }
      setCurrentProject({ name: '', client: '', startDate: '', endDate: '' });
      setIsModalOpen(false);
    } catch (error) {
      console.error(error);
    }
  };

  const confirmDelete = (project) => {
    setProjectToDelete(project);
    setIsDeleteModalOpen(true);
  };

  const handleDelete = async () => {
    try {
      await deleteProject(projectToDelete.id);
      setProjects(projects.filter(p => p.id !== projectToDelete.id));
      setIsDeleteModalOpen(false);
      setProjectToDelete(null);
    } catch (error) {
      console.error(error);
    }
  };

  const openModalForEdit = (project) => {
    setCurrentProject(project);
    setIsEditing(true);
    setIsModalOpen(true);
  };

  const openModalForAdd = () => {
    setCurrentProject({ name: '', client: '', startDate: '', endDate: '' });
    setIsEditing(false);
    setIsModalOpen(true);
  };

  return (
    <Layout>
      <div className="project-list-container">
        <div className="project-table-wrapper">
          <h1>Project List</h1>
          <table className="project-table table table-striped">
            <thead>
              <tr>
                <th>Name</th>
                <th>Client</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {projects.map((project) => (
                <tr key={project.id}>
                  <td onClick={() => openModalForEdit(project)}>{project.name}</td>
                  <td onClick={() => openModalForEdit(project)}>{project.client}</td>
                  <td onClick={() => openModalForEdit(project)}>{project.startDate}</td>
                  <td onClick={() => openModalForEdit(project)}>{project.endDate}</td>
                  <td>
                    <button className="btn btn-primary btn-sm" onClick={() => openModalForEdit(project)}>Edit</button>
                    <button className="btn btn-danger btn-sm" onClick={() => confirmDelete(project)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="button-container">
            <button className="btn btn-success" onClick={openModalForAdd}>Add New Project</button>
          </div>
        </div>
        <AddProjectModal
          isOpen={isModalOpen}
          onRequestClose={() => setIsModalOpen(false)}
          onSubmit={handleSubmit}
          project={currentProject}
          setProject={setCurrentProject}
          isEditing={isEditing}
        />
        <ConfirmDeleteModal
          isOpen={isDeleteModalOpen}
          onRequestClose={() => setIsDeleteModalOpen(false)}
          onConfirm={handleDelete}
        />
      </div>
    </Layout>
  );
};

export default ProjectList;
